cells.data:
header {
  border: 1px solid black;
  background: green;
  color: white;
  margin:10px;
  border-radius: 5px;
  padding-left: 5px;
  padding: 10px 10px;
}

article {
  height: 200px;
  width: 72%;
  border: 1px solid black;
  border-radius: 5px;
  margin: 0px 10px 10px;
  background: rgba(94, 176, 224, 1);
  float: left;
  padding: 5px 5px 0 5px;
}

aside {
  height: 150px;
  width: 23%;
  border: 1px solid black;
  border-radius: 5px;
  float: left;
  background: rgb(249, 193, 140);
  padding: 5px 0 0 5px;
}

footer {
  float: clear;
  border: 1px solid black;
  border-radius: 5px;
  background: rgb(119, 87, 106);
  margin: 10px;
  color: #fff;
  clear: both;
  padding: 5px;
}

header ul li {
  display: inline-block;
  padding: 5px;
}
